import React, { useState } from "react";
import { useDebouncedCallback } from "use-debounce";

import intercambio from '../../../assets/intercambio.png';

function Temperatura () {

  const [input, setInput] = useState("");
  const [cambio, setCambio] = useState("");
  const [desde, setDesde] = useState("C");
  const [hasta, setHasta] = useState("F");

  const convTemperatura = (temperatura) => {
    let response;
    console.log(temperatura);
    console.log(desde);
    console.log(hasta);
    switch (desde) {
      case "C":
        switch (hasta) {
          case "C":
            //console.log("de Celsius a Celsius");
            return temperatura;
            break;
          case "F":
            //console.log("de Celsius a Farhenheait"); + 
            return (temperatura * 1.8 + 32)
            break;
          case "K":
            //console.log("de Celsius a Kelvin");
            return (parseFloat(temperatura) + parseFloat(273.15));
            break;

          default:
            break;
        }
        break;
      case "F":
        switch (hasta) {
          case "C":
            //console.log("de Farenheit a Celsius");
            return ((temperatura - 32) / 1.8);
            break;
          case "F":
            //console.log("de Farenheit a Farhenheait");
            return temperatura;
            break;
          case "K":
            //console.log("de Farenheit a Kelvin");
            break;

          default:
            break;
        }

        break;
      case "K":
        switch (hasta) {
          case "C":
            //console.log("de Kelvin a Celsius");
            return (parseFloat(temperatura) - parseFloat(273.15));
            break;
          case "F":
            //console.log("de Kelvin a Farhenheait");
            break;
          case "K":
            //console.log("de Kelvin a Kelvin");
            return temperatura;
            break;

          default:
            break;
        }

        break;
      default:
        break;
    }
    return temperatura *= 1.2;
  }

  const debounced = useDebouncedCallback((value) => {
    console.log(value.target.value);
    //console.log(desde.value)
    setInput(value.target.value);
    console.log(input);
    setCambio(convTemperatura(value.target.value))
    //setCambio(value.target.value * 1.2);
    //value.target.value = "";
  }, 500);

  const handleChangeDesde = e =>{
    console.log(e.target.value);
    setDesde(e.target.value);
    console.log(desde);
  };
  const handleChangeHasta = e =>{
    console.log(e.target.value);
    setHasta(e.target.value);
    console.log(hasta);
  };

  return (
    <div className="temperatura">
      Temperatura
      <div className="camposdecambio">
        <input type="text" className="convertir" onChange={(e) => debounced(e)} name="convertir1" id="convertir1" />
        <button><img src={intercambio} alt="intercambio" className="intercambio" id="intercambio" /></button>
        <input defaultValue={cambio} type="text" className="convertir" name="convertir2" id="convertir2" />
      </div>

      <div className="unidades">

        <label htmlFor="desde">Desde:</label>
        <select name="desde" id="desde" onChange={(e) => handleChangeDesde(e)}>
          <option value="C">Celsius</option>
          <option value="F">Fahrenheit</option>
          <option value="K">Kelvin</option> 
        </select>
        <p>{desde}</p>

        <label htmlFor="hasta">Hasta:</label>
        <select name="hasta" id="hasta" onChange={(e) => handleChangeHasta(e)}>
          <option value="F">Fahrenheit</option>
          <option value="C">Celsius</option>
          <option value="K">Kelvin</option>
        </select>
      </div>
    </div>
  )

}

export default Temperatura;
